con:config.script:
import com.eviware.soapui.model.testsuite.TestStep;
import com.eviware.soapui.model.testsuite.TestStepResult;
import com.eviware.soapui.model.testsuite.TestStepResult.TestStepStatus;


String breadcrumb_urls = context.expand('${#TestCase#breadcrumb_urls}');
String expected_breadcrumbs = context.expand('${#TestCase#expected_breadcrumbs}');

String[] arr_breadcrumb_urls = breadcrumb_urls.split(",");
String[] arr_expected_breadcrumb = expected_breadcrumbs.split("\\]");
int index = 0;
for(String url in arr_breadcrumb_urls){
	log.info "Processing -- " + url;
	testRunner.testCase.setPropertyValue("url",url);
	testRunner.testCase.setPropertyValue("expected_breadcrumb",arr_expected_breadcrumb[index] + "]");
	index++;
	TestStep api_teststep = testRunner.testCase.getTestStepByName("API_breadcrumb");
	TestStepResult results = api_teststep.run(testRunner,context);
	if(results.getStatus() != TestStepStatus.OK){
		String[] messages = results.getMessages();
		assert false : messages;
	}
	else{
		assert true;
	}

}

con:configuration.scriptText:
import groovy.json.JsonSlurper;

String response = messageExchange.getResponseContent();
def jsonHolder = new JsonSlurper().parseText(response);

boolean success_flag = jsonHolder.success;
assert jsonHolder.success == true;

assert jsonHolder.errors.size() == 0 : jsonHolder.errors

String[] breadcrumbs_names = jsonHolder.data.breadcrumbs.name;
String[] breadcrumbs_urls = jsonHolder.data.breadcrumbs.url;

assert breadcrumbs_names.size() != 0;

def expected_json = new JsonSlurper().parseText(context.expand('${#TestCase#expected_breadcrumb}'))

String[] expected_breadcrumb_names = expected_json.name;
String[] expected_breadcrumb_urls = expected_json.url;

int index = 0;
for(String breadcrumb_name in breadcrumbs_names){
	String breadcrumb_url = breadcrumbs_urls[index];
	String expected_breadcrumb_name = expected_breadcrumb_names[index];
	String expected_breadcrumb_url = expected_breadcrumb_urls[index];
	index++;
	assert breadcrumb_name == expected_breadcrumb_name;
	assert breadcrumb_url == expected_breadcrumb_url;
}

