	}
}

import groovy.json.JsonSlurper;
import java.util.Random;

String response = messageExchange.getResponseContent();
def jsonHolder = new JsonSlurper().parseText(response);

boolean success_flag = jsonHolder.success;

if (jsonHolder.errors.size != 0)
{
	String error_message= jsonHolder.errors;
	log.info error_message;
	assert error_message.contains("No Destinations Found");
}
if (jsonHolder.errors.size() == 0)
{
int destination_count = jsonHolder.data.destinations.id.size();
int count_before = Integer.parseInt(context.expand('${#TestCase#destination_count}'));

messageExchange.modelItem.testCase.setPropertyValue("destination_count",destination_count.toString());
assert jsonHolder.data.total_count > 0;
assert destination_count <= count_before;

//Get random destination details.

int randomNum = new Random().nextInt((destination_count - 1) + 1);
log.info randomNum
int destination_id = jsonHolder.data.destinations.id[randomNum];
String destination_name = jsonHolder.data.destinations.name[randomNum];
String best_time_to_visit = jsonHolder.data.destinations.best_time_to_visit[randomNum];

messageExchange.modelItem.testCase.setPropertyValue("destination_id",destination_id.toString());
messageExchange.modelItem.testCase.setPropertyValue("destination_name",destination_name);
messageExchange.modelItem.testCase.setPropertyValue("best_time_to_visit",best_time_to_visit);




//verify price ranges 
int min_price = Integer.parseInt(context.expand('${#TestCase#price_range}').split("-")[0]);
int max_price = Integer.parseInt(context.expand('${#TestCase#price_range}').split("-")[1]);

String[] budgets = jsonHolder.data.destinations.budget;
for(String budget in budgets){
	budget = budget.replace(",","");
	budget = budget.trim();
	int budget_in_Int = Integer.parseInt(budget);
	if(!(budget_in_Int >= min_price && budget_in_Int <= max_price)){
		assert false : "Expexted budget range [" + min_price +"-"+ max_price +"] and actual budget is [" + budget + "]";
	}
}




//verify best time to visit

String[] besttime = jsonHolder.data.destinations.best_time_to_visit;
String month_range = context.expand('${#TestCase#months}');
int min_month = Integer.parseInt(month_range.split("-")[0]);
int max_month = Integer.parseInt(month_range.split("-")[1]);


for(String time in besttime){
	String[] time_parsed = time.split("\\..");

	int first_month = Integer.parseInt(time_parsed[0]);
	int second_month = Integer.parseInt(time_parsed[1]);
	if(!((first_month >= min_month && first_month <= max_month) || (second_month >= min_month && second_month <= max_month))){
	assert false : "best time to visit = " + time + " and months in input= "+ month_range;	
	}
}

//verify trip days 
String days = context.expand('${#TestCase#days}');
int min_days = Integer.parseInt(days.split("-")[0]);
int max_days = Integer.parseInt(days.split("-")[1]);

Integer[] min_days_destination = jsonHolder.data.destinations.min_days;
Integer[] max_days_destination = jsonHolder.data.destinations.max_days;
int index = 0;
for(int min_day in min_days_destination){
	int max_day = max_days_destination[index];
	index++;

	if(!(min_day <= max_days && max_day >= min_days)){
	//if(!((min_days >= min_day && min_days <= max_day)&& (max_days >= min_day && max_days <= max_day))){
		assert false : "Destination range at [" + index+1 + "] index doesn't match. Expected days range [" + min_days + "-" + max_days + "] and actual range in response [" + min_day +"-"+max_day+"]";
	}
}
}

