assert success_flag == true;

assert jsonHolder.errors.size() == 0;

assert jsonHolder.data.base_url != ""

if(jsonHolder.data.attractions != []){
int[] city_ids = jsonHolder.data.attractions.city_id;
int index = 0;
for(int city_id in city_ids){
	int ranking = jsonHolder.data.attractions.city_ranking[index];
	String city_name = jsonHolder.data.attractions.city_name[index];
	String attraction_name = jsonHolder.data.attractions.name[index];
	int image_id = jsonHolder.data.attractions.image_info[index].image_id;
	String large_img_id = jsonHolder.data.attractions.image_info[index].large_img_ids;
	String l_img = jsonHolder.data.attractions.image_info[index].l_img;
	String m_img = jsonHolder.data.attractions.image_info[index].m_img;
	String large_imgs = jsonHolder.data.attractions.image_info[index].large_imgs;

	assert ranking != null;
	assert city_name != "";
	assert attraction_name != "";
	assert image_id != null;
	assert large_img_id != "";
	assert l_img != "";
	assert m_img != "";
	assert large_imgs != "";
	index++;
}

}

