assert success_flag == true;

assert jsonHolder.errors.size() == 0;
//Verify sort order
boolean sorted;
String duration = jsonHolder.data.packages.days;
log.info "Duration of records" +duration
String[] parsed_duration=duration.split(",");
for (int i=1;i<parsed_duration.size()-3;i++)
{
	if (parsed_duration[i].compareTo(parsed_duration[i+1])<=0)
{
	log.info "Comparing duration..." +parsed_duration[i]+" and "+parsed_duration[i+1]
	sorted=true;
}
}
assert sorted==true;

