import groovy.json.JsonSlurper;

String response = messageExchange.getResponseContent();
def jsonHolder = new JsonSlurper().parseText(response);

boolean success_flag = jsonHolder.success;
assert success_flag == true;

assert jsonHolder.errors.size() == 0;
int count=0;
package_id = jsonHolder.data.packages.id;
ArrayList<Integer> p_ids = new ArrayList<Integer>();
for( String pkg_ids in package_id)
{
	pkg_ids = pkg_ids.replace(",","");
	p_ids.add(Integer.parseInt(pkg_ids));

}
String ids_to_compare=(context.expand('${#TestCase#ids}'));
String[] parsed_ids_to_compare= ids_to_compare.split(",");
int id_size=parsed_ids_to_compare.size();

boolean flag=false;
for (int i=0;i<id_size-1;i++)
{
	String temp;
	temp=parsed_ids_to_compare[i];
	parsed_ids_to_compare[i]=parsed_ids_to_compare[i+1].trim();
	parsed_ids_to_compare[i+1]=temp.trim();
	if (parsed_ids_to_compare[i].contains("]"))
	{
		parsed_ids_to_compare[i]=parsed_ids_to_compare[i].replace("]","");
	}
}
for (int i=0;i<id_size;i++)
{
	for(int j=0;j<p_ids.size();j++)
	{
log.info parsed_ids_to_compare[i];
log.info p_ids[i];

if(Integer.parseInt(parsed_ids_to_compare[i])==p_ids[j])
{
	count=count+1;
}
}
}
assert count>1;

